eight        self.color = nil        self.font = nil    }    #if os(iOS)    public init(image: UIImage?, width: CGFloat? = nil, height: CGFloat? = nil) {                if let image = image {            self.image = Image(uiImage: image)        } else {            self.image = nil        }                self.width = width        self.height = height        self.color = nil        self.font = nil    }#elseif os(macOS)    public init(image: NSImage?, width: CGFloat? = nil, height: CGFloat? = nil) {                if let image = image {            self.image = Image(nsImage: image)        } else {            self.image = nil        }                self.width = width        self.height = height        self.color = nil        self.font = nil    }#endif}